mapper.select:
SELECT id, warehouse_id as warehouseId, type, expense_date as expenseDate, cost
FROM Expense
WHERE id = #{id}

SELECT *
FROM Expense





                    AND id = #{option.value}


                    AND warehouse_id = #{option.value}


                    AND expense_date >= #{option.value}


                    AND expense_date <= #{option.value}


                    AND cost >= #{option.value}


                    AND cost <= #{option.value}

SELECT SUM(cost)
FROM Expense





                    AND id = #{option.value}


                    AND warehouse_id = #{option.value}


                    AND expense_date >= #{option.value}


                    AND expense_date <= #{option.value}


                    AND cost >= #{option.value}


                    AND cost <= #{option.value}
